import React, { Component } from 'react'
import Chart from "chart.js";


export default class Graph extends Component {
    chartRef = React.createRef();

    componentDidMount() {
      // filter out data that will not be graphed
      let graph = this.props.graph
      graph = graph.split(/(\s+)/).filter((value) => {
        return value.includes("e") && value.length > 6
      })
      graph.shift()
      // extract data that will be graphed
      let data_set = []
      let data_point = {}
      let counter = 0
      let nu_counter = 0
      let total_counter = 6
      graph.forEach((value) => {
        if(counter - nu_counter === 0) {
          nu_counter += 9
          data_point.x = Number(value)
        }
        if(counter - total_counter === 0) {
          total_counter += 9
          data_point.y = Number(value)
          data_set.push(data_point)
        }
        counter ++
      })
      console.log(data_set)
        const myChartRef = this.chartRef.current.getContext("2d");

        new Chart(myChartRef, {
            type: "scatter",
            data: {
                datasets: [
                    {
                      label: 'Scatter Dataset',
                      data: data_set
                    }
                ]
            },
            options: {
                scales: {
                  xAxes: [{
                    display: true,
                    type: 'logarithmic'
                  }],
                  yAxes: [{
                    display: true,
                    type: 'linear'
                  }]
                }
            }
        });
    }
    render() {
        return (
            <div >
                <canvas
                    id="myChart"
                    ref={this.chartRef}
                />
            </div>
        )
    }
}
