import React, { Component } from 'react';
import {List} from 'semantic-ui-react'


class Lines extends Component {

  // Fromat selection to display top 50 brightest lines
  renderList = () => {
    let index = 0
    let selectionWithIndex = this.props.selection.map((value) => {
      index += 1
      return [index - 1, value]
    })
    selectionWithIndex.splice(0, 7)
    console.log(selectionWithIndex)
    console.log(this.props.comFile.data[0])
  }

  render() {
    return (
      <List divided relaxed>
      {this.renderList()}
    {this.props.selection.map((item) => {
      return <List.Item><List.Content>
        <List.Header>{item}</List.Header>
      </List.Content></List.Item>
    })}
  </List>
    );
  }

}

export default Lines;
