import React, { Component } from 'react'
import Chart from "chart.js";


export default class Graph extends Component {
    chartRef = React.createRef();

    componentDidMount() {
      // filter out data that will not be graphed
      let graph = this.props.graph
      graph = graph.split(/(\s+)/).filter((value) => {
        return value.includes("e") && value.length > 6
      })
      graph.shift()
      // extract data that will be graphed
      console.log(graph)
      let nu = []
      let reflc = []
      let nu_counter = 1
      let reflc_counter = 7
      graph.forEach((value) => {
        if(graph.indexOf(value) - nu_counter === 0) {
          nu_counter += 9
          nu.push(value)
        }else if(graph.indexOf(value) - reflc_counter === 0) {
          reflc_counter += 9
          reflc.push(value)
        }
        console.log(graph.indexOf(value) - reflc_counter)
      })
      console.log(nu)
      console.log(reflc)
        const myChartRef = this.chartRef.current.getContext("2d");

        new Chart(myChartRef, {
            type: "line",
            data: {
                //Bring in data
                datasets: [
                    {
                      fill: false,
                        label: "NU",
                        data: [],
                    },
                    {
                      fill: false,
                      label: "REFLC",
                      data: [],
                    }
                ]
            },
            options: {
                scales: {
                  xAxes: [{
                    display: true,
                    type: 'logarithmic'
                  }],
                  yAxes: [{
                    display: true,
                    type: 'logarithmic'
                  }]
                }
            }
        });
    }
    render() {
        return (
            <div >
                <canvas
                    id="myChart"
                    ref={this.chartRef}
                />
            </div>
        )
    }
}
